function main() {
    var width = params.width;
    var height = params.height;

    return cylinder({
            r: (width / 2) - 1, 
            h: 1
        }).translate([1, 1, height]);
}

function main() {
    var width = params.width;
    var height = params.height;

    return torus({
        ri: 1,
        ro: (width / 2) - 1
    }).translate([1, 1, height]);
}

function main(){
    var width = params.width;

    var beads = []
    var r = width / 2, n = 10, theta = 0
    var delta = 2 * Math.PI / n
    for (var i = 0; i < n; i++){                
        var x = r * Math.cos(theta)
        var y = r * Math.sin(theta)
        beads.push('')
        theta = theta + delta
    }                        
    return beads.join('\n') 
}
